import { StyleSheet, Text, View, FlatList} from 'react-native';
import React, { useState, useEffect } from 'react';
import { Card } from 'react-native-paper';


export default function App() {
  const [dados, setDados] = useState([]);

  useEffect(() => {
    fetch("https://api.apify.com/v2/key-value-stores/TyToNta7jGKkpszMZ/records/LATEST?disableRedirect=true")
      .then(res => res.json())
      .then((data) => setDados(data.infectedByRegion))
      .catch(error => console.error(error));
  }, []);

  return (
    <View style={styles.container}>
     {dados?.map((dado)=> (
        <View key={dado.id}>
          <Card>
            <Card.Title>Infectados Covid</Card.Title>
            <Card.Content>
              <Text>{dado.state}</Text>
              <Text>{dado.count}</Text>
            </Card.Content>
          </Card>
        </View>
      ))}
    </View>
  );
}

const styles = StyleSheet.create({
  container:{
    marginTop: 100,
  }
});
