import { StyleSheet, Text, View, FlatList} from 'react-native';
import React, { useState, useEffect } from 'react';
import { Card, Title } from 'react-native-paper';
import { CovidCard } from './covidCard';


export default function App() {
  const [dados, setDados] = useState([]);
  const [currentJoke, setCurrentJoke] = useState(0);

  useEffect(() => {
    fetch("https://api.apify.com/v2/key-value-stores/TyToNta7jGKkpszMZ/records/LATEST?disableRedirect=true")
      .then(res => res.json())
      .then((data) => setDados(data.infectedByRegion))
      .catch(error => console.error(error));
  }, []);


  const handleNextState = () => {
    if (currentJoke + 1 < dados.length) {
      setCurrentJoke(currentJoke + 1);
    }
  }

  return (
    <View style={styles.container}>
      <CovidCard data={dados} onNext={handleNextState}></CovidCard>
    </View>
  );
}

const styles = StyleSheet.create({
  container:{
    marginTop: 100,
  }
});
